    <script type="text/javascript">
    $(function() {
        // 底部关注的弹出
        var $share_icons = $('.share_icons'),
            $focus_wb = $('.focus_wb'),
            $focus_wx = $('.focus_wx');

        var $quick_links = $('.quick_links'),
            $quick_links_con = $quick_links.find('dd');

        var spic_index, $spic_con = $('.spic_con'),
            $s_pic = $spic_con.find('li');

        $share_icons.on('mouseover', 'a', function(e) {
            var $this = $(this);
            var index = $this.index();

            if (index == 0) {
                $focus_wb.show();
                $focus_wx.hide();

            } else if (index == 1) {
                $focus_wb.hide();
                $focus_wx.show();
            }
        });

        $(document).on('click', function() {
            $focus_wx.hide();
            $focus_wb.hide();
        });

        // 底部点击出现
        $quick_links.on('click', function() {
            $quick_links_con.slideToggle();
        });


        var mySwiper = new Swiper('.swiper-container', {
            onSlideChangeEnd: function(swiper) {
                $s_pic.eq(swiper.activeIndex).addClass('on');
            },
            onSlideChangeStart: function(swiper) {
                $s_pic.removeClass('on');
            }
        })

        $s_pic.on('click', function(e) {
            var $this = $(this);
            spic_index = $this.index();
            mySwiper.slideTo(spic_index);
        });

    });
    </script>
